const form=document.getElementById("formContact");const fname=document.getElementById("formName");const phone=document.getElementById("formPhone");const email=document.getElementById("formEmail");const message=document.getElementById("formMessage");form.addEventListener("submit",(form)=>{form.preventDefault();formValidate()});function error(element,message){const inputInvalid=element.parentElement;const displayError=inputInvalid.querySelector(".error");displayError.innerText=message;inputInvalid.classList.add("invalid");inputInvalid.classList.remove("valid")}
function success(element){const inputValid=element.parentElement;const removeError=inputValid.querySelector(".error");removeError.innerText="";inputValid.classList.add("valid");inputValid.classList.remove("invalid")}
function emailIsValid(email){const re=/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;return re.test(String(email).toLowerCase())}
function formValidate(){const fnameValue=fname.value.trim();const phoneValue=phone.value.trim();const emailValue=email.value.trim();const messageValue=message.value.trim();console.log(fnameValue);console.log(emailValue);if(fnameValue===""){error(fname,"Don't forget your name!")}else{success(fname)}
if(phoneValue===""){error(phone,"Don't forget your number!")}else{success(phone)}
if(emailValue===""){error(email,"Don't forget your email!")}else if(!emailIsValid(email)){error(email,"That email isn't real :(")}else{success(email)}
if(messageValue===""){error(message,"You gotta say something!")}else{success(message)}}
const img1=document.getElementById("img1details");const img2=document.getElementById("img2details");const img3=document.getElementById("img3details");function showDetails1(){img1.classList.toggle("hide")}
function showDetails2(){img2.classList.toggle("hide")}
function showDetails3(){img3.classList.toggle("hide")}
const filterButtons=document.querySelectorAll('.filter-btn');const projects=document.querySelectorAll('.img-wrap');filterButtons.forEach(button=>{button.addEventListener('click',function(){const category=this.getAttribute('data-category');projects.forEach(project=>{if(project.getAttribute('data-category')===category||category==='all'){project.style.display='block'}else{project.style.display='none'}})})});document.addEventListener('DOMContentLoaded',()=>{const darkToggle=document.getElementById('dark-toggle');const savedDarkMode=localStorage.getItem('darkMode');if(savedDarkMode==='enabled'){document.body.classList.add('dark-mode')}
darkToggle.addEventListener('click',()=>{document.body.classList.toggle('dark-mode');if(document.body.classList.contains('dark-mode')){localStorage.setItem('darkMode','enabled')}else{localStorage.removeItem('darkMode')}})})